      try {
        const user = supabase.auth.user()
        if (!user) return

        const { data, error } = await supabase
          .from('profiles')
          .select('*')
          .eq('id', user.id)
          .single()

        if (error) throw error
        profile.value = { ...profile.value, ...data, email: user.email }
        isSignedIn.value = true
      } catch (error) {
        console.error('Error reading profile:', error.message)
